#!/usr/bin/env python
# -*- coding:utf8 -*-


from flask import Flask, request, make_response, jsonify
from cv_parser.api_cv_parser import CvParser

app = Flask(__name__)

parser = CvParser()


@app.route('/cvparse', methods=['POST'])
def cvparse():
    channel = request.form['channel']
    pagecontent = request.form['pagecontent']

    result = parser.parser(htmlContent=pagecontent, cvFrom=channel)

    return make_response(jsonify(result))


if __name__ == '__main__':
    app.run('0.0.0.0', 9627, debug=True)


